mapper.update:
update blog 
set title = #{title}

	,logo = #{logo}

where no = #{no}

mapper.select:
select no, title, logo
from blog
where no = #{no}

select blog.no, title, logo
from blog join user on blog.no = user.no
where id = #{id}

select no, title, logo
from blog
where title = #{blog.title}
limit #{pageInfo.offset}, #{pageInfo.resultPerPage}
